import React, { useState } from 'react'
import Head from 'next/head'
import { useRouter } from 'next/router'
import { getScreenshot } from './api/chromium'



const Home: React.FC = () => {

  const [inputIP, setInputIP] = useState('')
  const router = useRouter()

  // const file = getScreenshot('https://myip.ms/')

  function handleClick(text) {
    alert(text)
  }


  return (
    <div className="container">
      <Head>
        <title>Primefox - Get Range IP</title>
      </Head>

      <div className="card">
        <h3>Buscar . . .</h3>
        <input type="text" placeholder="Digite Host ou IP" onChange={e => setInputIP(e.target.value)} />
        {/* <button type="submit" onClick={() => router.push(`/getrange/${inputIP}`)}>Get Range</button> */}
        <button type="submit" onClick={handleClick('joia')}>Get Range</button>
      </div>

      <div className="bottom">
        <a href="http://primefox.com.br" target="_blank">Primefox</a>
        <img src="favicon.ico" alt="print" />
      </div>

    </div>
  )
}

export default Home;
